INSERT INTO `products` (`id`, `type_id`, `product_no`, `page_id`, `supplier_id`, `name`, `description`, `category`, `price`, `image`, `thumb_image`, `flowerwyz_image`, `status`, `ord`, `created`) VALUES
(1, 1, 'B9-4833', NULL, NULL, 'Lights of the Season Centerpiece', 'The Lights of the Season Centerpiece is an exquisite way to bring the magic of the holiday season to your celebrations with family and friends. Brilliant red carnations and mini carnations are gorgeously arranged amongst variegated holly and an assortment of holiday greens. Accented with red glass balls and a bright red wired ribbon, this arrangement encircles 3 red taper candles to bring the perfect glow to your festivities', 'c', 49.95, 'https://www.floristone.com/flowers/products/B9-4833.jpg', 'https://www.floristone.com/flowers/thumbnails/B9-4833_t.jpg', 'https://www.flowerwyz.com/images/send-flowers.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(2, 1, 'B10-4355', NULL, NULL, 'The Holiday Happiness Bouquet', 'The Holiday Happiness Bouquet', 'c', 47.95, 'https://www.floristone.com/flowers/products/B10-4355.jpg', 'https://www.floristone.com/flowers/thumbnails/B10-4355_t.jpg', 'https://www.flowerwyz.com/images/cheap-flower-delivery.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(3, 1, 'B10-4368', NULL, NULL, 'Celebration of the Season Centerpiece', 'The Celebration of the Season Centerpiece is a grand display of holiday elegance. Red roses and spray roses pop against a backdrop of assorted holiday greens and variegated holly that beautifully encircle three red taper candles. Accented with gold pinecones and gold metallic brocade ribbon this centerpiece creates a warm and enchanting glow to benefit their holiday festivities.', 'c', 74.95, 'https://www.floristone.com/flowers/products/B10-4368.jpg', 'https://www.floristone.com/flowers/thumbnails/B10-4368_t.jpg', 'https://www.flowerwyz.com/images/online-flowers.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(4, 1, 'B10-4787', NULL, NULL, 'The Spirit of the Season Bouquet', 'The Spirit of the Season Bouquet bursts with beauty and cheer to dazzle your special recipient with its holiday beauty. Brilliant red spray roses and mini carnations are brought together with burgundy mini carnations, red hypericum berries and assorted holiday greens in a clear glass vase accented with a thick red designer ribbon to create a special sentiment of seasonal wishes and caring kindness', 'c', 49.95, 'https://www.floristone.com/flowers/products/B10-4787.jpg', 'https://www.floristone.com/flowers/thumbnails/B10-4787_t.jpg', 'https://www.flowerwyz.com/images/flowers-delivered.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(5, 1, 'B12-4421', NULL, NULL, 'The Winter Elegance Bouquet', 'The Winter Elegance Bouquet offers warm wishes and bright sentiments for a wonderful holiday season! Rich red roses and peppermint carnations are delicately arranged amongst holiday greens to create a festive display. Arriving in a clear glass bubble bowl and accented with a red and white plaid ribbon, this arrangement is full of seasonal sophistication.', 'c', 69.95, 'https://www.floristone.com/flowers/products/B12-4421.jpg', 'https://www.floristone.com/flowers/thumbnails/B12-4421_t.jpg', 'https://www.flowerwyz.com/images/best-online-flowers.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(6, 1, 'B13-4834', NULL, NULL, 'The Holiday Enchantment Bouquet', 'The Holiday Enchantment Bouquet is blooming with bright seasonal cheer to bring Christmas joy to your special recipient. Rich red roses are surrounded by white Peruvian lilies, variegated holly and assorted holiday greens perfectly arranged in a clear glass vase tied at the neck with a candy cane striped ribbon to create a fantastic holiday look.', 'c', 54.95, 'https://www.floristone.com/flowers/products/B13-4834.jpg', 'https://www.floristone.com/flowers/thumbnails/B13-4834_t.jpg', 'https://www.flowerwyz.com/images/best-flower-delivery.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(7, 1, 'B14-4356', NULL, NULL, 'The Merry & Bright Bouquet', 'The Merry & Bright Bouquet is a charming display of seasonal cheer. Vibrant red roses, white spray roses, red hypericum berries and assorted holiday greens create a stunning look with green glass balls and green taffeta ribbon nestled within the arrangement. Arranged in a red glass cube vase, this bouquet is guaranteed to make their holiday both merry and bright.', 'c', 49.95, 'https://www.floristone.com/flowers/products/B14-4356.jpg', 'https://www.floristone.com/flowers/thumbnails/B14-4356_t.jpg', 'https://www.flowerwyz.com/images/cheap-flowers.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(8, 1, 'B14-4426', NULL, NULL, 'Candy Cane Lane Bouquet', 'The Candy Cane Lane Bouquet is a festive and fun way to celebrate the arrival of the holiday season. Bright red roses and mini carnations are offset by snowy white chrysanthemums and assorted holiday greens, beautifully arranged in a white wash basket and accented with 3 candy canes to offer holiday joy and cheer to your special recipient.', 'c', 59.95, 'https://www.floristone.com/flowers/products/B14-4426.jpg', 'https://www.floristone.com/flowers/thumbnails/B14-4426_t.jpg', 'https://www.flowerwyz.com/images/send-flowers-online.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(9, 1, 'B14-4832', NULL, NULL, 'Christmas Peace Bouquet', 'The Christmas Peace Bouquet captures the elegance of the season at every turn. White roses, red carnations, white stock, green button poms, holly and assorted holiday greens are arranged perfectly in a modern clear square glass vase to create a gift that extends warm wishes for a season of peace and togetherness', 'c', 49.95, 'https://www.floristone.com/flowers/products/B14-4832.jpg', 'https://www.floristone.com/flowers/thumbnails/B14-4832_t.jpg', 'https://www.flowerwyz.com/images/flowers-delivery.png', 'Y', 0, '2016-03-02 11:49:00'),
(10, 1, 'B16-4830', NULL, NULL, 'The Season''s Glow Centerpiece', 'The Season''s Glow Centerpiece blooms with winter elegance and holiday enchantment to grace your Christmas celebrations. White LA hybrid lilies, carnations, chrysanthemums and statice are arranged amongst holiday greens and accented with gold pinecones and a gold plaid French wired ribbon for an exquisite look. Arranged to encircle 3 white taper candles, this stunning arrangement will bring a sweet sophistication to your holiday festivities', 'c', 59.95, 'https://www.floristone.com/flowers/products/B16-4830.jpg', 'https://www.floristone.com/flowers/thumbnails/B16-4830_t.jpg', 'https://www.flowerwyz.com/images/sending-flowers.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(11, 1, 'B17-4362', NULL, NULL, 'The Winter Wishes Bouquet', 'The Winter Wishes Bouquet is a snow-filled display of seasonal elegance. White roses, chrysanthemums, and Asiatic Lilies create winter magic seated in a gold basket and accented with green hypericum berries and a gold plaid ribbon, making this a wish for a season abundant in beauty and togetherness.', 'c', 64.95, 'https://www.floristone.com/flowers/products/B17-4362.jpg', 'https://www.floristone.com/flowers/thumbnails/B17-4362_t.jpg', 'https://www.flowerwyz.com/images/online-flower-delivery.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(12, 1, 'B18-4374', NULL, NULL, 'The Miracle''s Light Hanukkah Bouquet', 'The Miracle''s Light Hanukkah Bouquet celebrates each miraculous moment of this festive and meaningful holiday with its abundance of beautiful blooms. Blue iris creates a splash of color against a backdrop of white roses and white Asiatic lilies accented with lush greens, silver pinecones and blue and silver organza ribbon, gorgeously situated in a clear glass ginger jar vase to create a luminous gift they will treasure throughout the eight days and nights of the Hanukkah season.', 'c', 64.95, 'https://www.floristone.com/flowers/products/B18-4374.jpg', 'https://www.floristone.com/flowers/thumbnails/B18-4374_t.jpg', 'https://www.flowerwyz.com/images/send-flowers-cheap.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(13, 1, 'F1-116', NULL, NULL, 'Baby Boy Balloon Bouquet', 'Festive balloon bouquet consisting of 6 latex balloons and 2 mylar Baby Boy balloons. Balloon types may vary.', 'b', 42.95, 'https://www.floristone.com/flowers/products/bb_bb.jpg', 'https://www.floristone.com/flowers/thumbnails/bb_bb_t.jpg', 'https://www.flowerwyz.com/images/cheap-flowers-delivered.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(14, 1, 'F1-CPP', NULL, NULL, 'The Peace Lily', 'The Peace Lily is a popular arrangement to send a message of tranquility and serenity. White blossoms emerging from dark leaves set in a natural wicker basket convey lasting peace and elegance.', 'p', 59.95, 'https://www.floristone.com/flowers/products/F1-CPP.jpg', 'https://www.floristone.com/flowers/thumbnails/F1-CPP_t.jpg', 'https://www.flowerwyz.com/images/flowers-for-delivery.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(15, 1, 'B3-4347', NULL, NULL, 'The Abundant Harvest Basket', 'The Abundant Harvest Basket is bejeweled in Autumn''s most majestic hues creating an arrangement of blooming beauty.  Dark orange Asiatic Lilies, bi-colored orange roses, burgundy miniature carnations, fuchsia spray roses, peach hypericum berries and millet grass are bountifully displayed in a natural wood basket to celebrate the inspiring beauty of this coming harvest season.', 'c', 49.95, 'https://www.floristone.com/flowers/products/B3-4347.jpg', 'https://www.floristone.com/flowers/thumbnails/B3-4347_t.jpg', 'https://www.flowerwyz.com/images/deliver-flowers.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(16, 1, 'D4-4898', NULL, NULL, 'Best Year Basket', 'The Best Year Basket, blossoming with gerbera daisies and stock, conveys your wishes that this coming year be the best year yet! Purple stock, orange carnations, yellow gerbera daisies, purple monte casino asters and lush greens are gorgeously arranged in a whitewash woodchip basket hugged by a sweet 10-inch seated plush bear tied with a lavender ribbon around the neck. Full of color and exuding a soft fragrance, this bouquet creates an exceptional birthday gift they are sure to love.', 'n', 59.95, 'https://www.floristone.com/flowers/products/D4-4898.jpg', 'https://www.floristone.com/flowers/thumbnails/D4-4898_t.jpg', 'https://www.flowerwyz.com/images/birthday-gifts-delivered.jpg', 'Y', 0, '2016-03-02 11:49:00'),
(17, 1, 'D5-4894', NULL, NULL, 'The Happy Blooms Basket', 'The Happy Blooms Basket brings together Asiatic lilies and carnations to create the perfect way to send your happy birthday wishes! Orange Asiatic lilies, lavender chrysanthemums, lavender carnations, purple monte casino asters, green button poms and lush greens are beautifully arranged within a rectangular whitewash willow handled basket accented with colorful curling ribbon. Arriving with a bright Mylar balloon exclaiming, "Happy Birthday," this flower arrangement will add to the celebration of their special day. GOOD basket includes 9 stems. Approx. 11"H x 12"W. BETTER basket includes 13 stems. Approx. 11"H x 15"W. BEST basket includes 18 stems. Approx. 12"H x 16"W.', 'o', 49.95, 'https://www.floristone.com/flowers/products/D5-4894.jpg', 'https://www.floristone.com/flowers/thumbnails/D5-4894_t.jpg', 'https://www.flowerwyz.com/images/birthday-gifts-delivery.jpg', 'Y', 0, '2016-03-02 11:49:01'),
(18, 1, 'D9-4913', NULL, NULL, 'The Lilies & More Bouquet', 'The Lilies & More Bouquet is a happy display of color and blooming beauty to send your special recipient your sweetest sentiments. Pink and yellow Asiatic lilies pop with color and a vibrant energy accented with lily grass and arranged within a clear glass pillow vase to create an incredible way to extend your cheeriest congratulations wishes.', 'v', 74.95, 'https://www.floristone.com/flowers/products/D9-4913.jpg', 'https://www.floristone.com/flowers/thumbnails/D9-4913_t.jpg', 'https://www.flowerwyz.com/images/floral-arrangements.jpg', 'Y', 0, '2016-03-02 11:49:01'),
(19, 1, 'S7-4450', NULL, NULL, 'Eternal Affection Arrangement', 'The Eternal Affection Arrangement is a peaceful offering of heartfelt sympathy. White gladiolus, Peruvian lilies, carnations, mini carnations and lush greens are beautifully arranged in a round whitewash handled basket to create a beautiful display of soft serenity.', 'fa', 59.95, 'https://www.floristone.com/flowers/products/S7-4450.jpg', 'https://www.floristone.com/flowers/thumbnails/S7-4450_t.jpg', 'https://www.flowerwyz.com/images/flowers-to-send.jpg', 'Y', 0, '2016-03-02 11:49:01'),
(20, 1, 'B23-4375', NULL, NULL, 'The Red Romance Rose Bouquet', 'The Red Romance Rose Bouquet will dazzle your special recipient with its expression of love and beauty this coming Valentine''s Day. Rich red roses and spray roses are gorgeously arranged in a clear glass vase, accented with a lovely mix of greens, to create a bouquet bursting with romantic intentions.', 'r', 69.95, 'https://www.floristone.com/flowers/products/B23-4375.jpg', 'https://www.floristone.com/flowers/thumbnails/B23-4375_t.jpg', 'https://www.flowerwyz.com/images/cheap-flowers-online.jpg', 'Y', 0, '2016-03-02 11:49:01'),
(21, 1, 'C30-4572', NULL, NULL, 'Fruit & Flowers Basket', 'The Fruit & Flowers Basket is a thoughtful gift that conveys your warmest sentiments. Pink roses, pink stock, orange spray roses, green gladiolus, variegated ivy, and lush greens are arranged amongst a collection of fruit, gorgeously seated in a large green basket to create a stunning gift.', 'x', 79.95, 'https://www.floristone.com/flowers/products/C30-4572.jpg', 'https://www.floristone.com/flowers/thumbnails/C30-4572_t.jpg', 'https://www.flowerwyz.com/images/birthday-flower-delivery.jpg', 'Y', 0, '2016-03-02 11:49:01'),
(22, 1, 'FA302', NULL, NULL, 'Red and White Floor Basket', 'A floor basket of red carnations and white gladioli. Appropriate for funeral home.', 'fb', 74.95, 'https://www.floristone.com/flowers/products/FA302.jpg', 'https://www.floristone.com/flowers/thumbnails/FA302_t.jpg', 'https://www.flowerwyz.com/images/flower-deliveries.jpg', 'Y', 0, '2016-03-02 11:49:01'),
(23, 1, 'F1-118', NULL, NULL, 'Get Well Balloon Bouquet', 'Balloon bouquet consisting of 6 latex balloons and 2 mylar Get Well balloons. Balloon types may vary.', 'b', 42.95, 'https://www.floristone.com/flowers/products/gw_bb.jpg', 'https://www.floristone.com/flowers/thumbnails/gw_bb_t.jpg', 'https://www.flowerwyz.com/images/cheapest-flower-delivery.jpg', 'Y', 0, '2016-03-02 11:49:01'),
(24, 1, 'F1-509', NULL, NULL, 'Enduring Spirit Pothos Basket', 'Honor their memory for years to come with a 6? trailing Pothos in a wicker basket. The Pothos plant does not require much light and is easy to maintain, yet it is lush, vibrant, and grows fast, making it an ideal and lasting condolence sentiment. Appropriate for the family''s home or the funeral home.', 'p', 39.95, 'https://www.floristone.com/flowers/products/F1-509.jpg', 'https://www.floristone.com/flowers/thumbnails/F1-509_t.jpg', 'https://www.flowerwyz.com/files/discount-flower-delivery.jpg', 'Y', 0, '2016-03-02 11:49:01'),
(25, 1, 'D9-4912', NULL, NULL, 'The New Dream Bouquet', 'The New Dream Bouquet blooms with roses and tulips to dress up their day in color and fresh beauty. Orange roses, yellow tulips, white spider chrysanthemums, purple double lisianthus and lush myrtle are exquisitely arranged in a clear glass vase accented with a mauve satin ribbon to create the perfect way to send your congratulations and warmest wishes.', 'v', 79.95, 'https://www.floristone.com/flowers/products/D9-4912.jpg', 'https://www.floristone.com/flowers/thumbnails/D9-4912_t.jpg', 'https://www.flowerwyz.com/images/flower-arrangements-ideas.jpg', 'Y', 0, '2016-03-02 11:49:01'),
(26, 1, 'E8-4816', NULL, NULL, 'The Soft Serenade Rose Bouquet', 'The Soft Serenade Rose Bouquet is a sunny and charming flower arrangement set to spread your warmest wishes. Pink roses, yellow spray roses, white hypericum berries, white limonium and lush greens are perfectly arranged within a clear glass bubble bowl vase to create a wonderful way to say thank you, get well or to extend your congratulations.', 'r', 79.95, 'https://www.floristone.com/flowers/products/E8-4816.jpg', 'https://www.floristone.com/flowers/thumbnails/E8-4816_t.jpg', 'https://www.flowerwyz.com/images/anniversary-flowers.jpg', 'Y', 0, '2016-03-02 11:49:01'),
(27, 1, 'C12-4400', NULL, NULL, 'The Wondrous Nature Bouquet', 'The Wondrous Nature Bouquet is bountifully bedecked with a dazzling display of color and beauty. Stargazer lilies stretch their fuchsia petals out amongst an arrangement of blue iris, white traditional daisies, orange mini carnations, purple statice, and yellow solidago in a round whitewash handled basket, creating a delightful bouquet your special recipient will adore.', 'o', 49.95, 'https://www.floristone.com/flowers/products/C12-4400.jpg', 'https://www.floristone.com/flowers/thumbnails/C12-4400_t.jpg', 'https://www.flowerwyz.com/images/floral-arrangement.jpg', 'Y', 0, '2016-03-02 11:49:01'),
(28, 1, 'C13-4840', NULL, NULL, 'The Basket of Cheer Bouquet', 'The Basket of CheerÂ® Bouquet sends your warmest wishes for happiness with each sunlit bloom! Yellow Asiatic lilies and traditional daisies are vibrant and beautiful arranged amongst pink carnations, pink Peruvian lilies and magenta mini carnations. Accented with lush greens and presented in a round whitewash handled basket, this arrangement is a sweet sentiment brought together to brighten your special recipient''s day. Approx. 11"H x 13"W', 'c', 44.95, 'https://www.floristone.com/flowers/products/C13-4840.jpg', 'https://www.floristone.com/flowers/thumbnails/C13-4840_t.jpg', 'https://www.flowerwyz.com/images/birthday-gifts-for-mom.jpg', 'Y', 0, '2016-03-02 11:49:01'),
